#! /usr/bin/env node

import '@babel/polyfill';

import {packageOf, currentModulePath} from '@tech_query/node-toolkit';

import Commander from 'commander';

import {which} from './core';

import {appendFileSync} from 'fs';

import {execSync} from 'child_process';


const meta = packageOf( currentModulePath() ).meta;

Commander
    .version( meta.version )
    .description( meta.description )
    .arguments('[name ...]')
    .option('-f, --ini-file <path>',  'Append result to an ini-like file')
    .option('-c, --NPM-config',  'Set result to local NPM configuration')
    .parse( process.argv );

const show_log = Commander.iniFile || Commander.NPMConfig;


(async () => {

    if (show_log)  console.time('Search');

    for (let name of Commander.args) {

        let path = await which( name );

        console.info(
            ((show_log || Commander.args[1])  ?  `${name}=`  :  '')  +  path
        );

        if ( Commander.iniFile )
            appendFileSync(Commander.iniFile,  `${name}=${path}\n`);
        else if ( Commander.NPMConfig )
            execSync(`npm set ${name} ${JSON.stringify( path )}`);
    }

    if (! show_log)  return;

    console.info('--------------------');

    console.timeEnd('Search');
})();

import {existsSync, readdirSync} from 'fs';

const Env = process.env;


var disk;
/**
 * @return {string[]} Acessible disk partitions of current Windows
 */
export  function getPartition() {

    return  disk  ||  (disk = 'CDEFGHIJKLMNOPQRSTUVWXYZ'.split('').map(disk => {

        disk = `${disk}:\\`;

        if (existsSync( disk )  &&  readdirSync( disk )[2])  return disk;

    }).filter( Boolean ));
}


var folder;
/**
 * @return {string[]} Acessible application folders of current Windows
 */
export  function getAppFolder() {

    return  folder  ||  (folder = [ ].concat(... getPartition().map(disk => {

        const list = [ ];

        for (let path of [
            Env.PROGRAMFILES, Env['ProgramFiles(x86)'], Env.LOCALAPPDATA
        ])
            if (existsSync(path = disk + path.slice(3)))  list.push( path );

        return list;
    })));
}

import {traverse, filter, which} from '../source/core';

import {statSync, readFileSync, unlinkSync, existsSync} from 'fs';

import {isAbsolute} from 'path';

import {getPartition, getAppFolder} from '../source/windows';

import {release} from 'os';

import { execSync } from 'child_process';


const directory = [
        'docs/ast',
        'docs/ast/source',
        'docs/css',
        'docs/file',
        'docs/file/source',
        'docs/function',
        'docs/image',
        'docs/script',
        'docs/script/prettify',
        'docs/test-file',
        'docs/test-file/test'
    ],
    NodeExe = `node${(process.platform === 'win32')  ?  '.exe'  :  ''}`;


describe('Core methods',  () => {
    /**
     * @test {traverse}
     */
    it('Traverse directory tree',  async () => {

        const folder = [ ];

        for await (let name of traverse('./docs'))
            if (statSync( name ).isDirectory())
                folder.push( name.replace(/\\/g, '/') );

        folder.should.be.eql( directory );
    });


    describe('Iterator filter',  () => {
        /**
         * @test {filter}
         */
        it('By pattern',  async () => {

            const folder = [ ];

            for await (let name of filter(
                traverse('./docs'), /(\\|\/)[^\\/.]+$/
            ))
                folder.push( name.replace(/\\/g, '/') );

            folder.should.be.eql( directory );
        });

        /**
         * @test {filter}
         */
        it('By count',  async () => {

            const folder = [ ];

            for await (let name  of  filter(traverse('./docs'), null, 2))
                folder.push( name.replace(/\\/g, '/') );

            folder.should.be.eql( directory.slice(0, 2) );
        });

        /**
         * @test {filter}
         */
        it('By pattern & count',  async () => {

            const folder = [ ];

            for await (let name of filter(
                traverse('./docs'), /(\\|\/)[^\\/.]+$/, 3
            ))
                folder.push( name.replace(/\\/g, '/') );

            folder.should.be.eql( directory.slice(0, 3) );
        });
    });

    /**
     * @test {which}
     */
    it('Search application executable',  async () => {

        const path = await which('node');

        isAbsolute( path ).should.be.true();

        path.should.be.endWith( NodeExe );
    });
});


describe('Windows dedicated',  () => {

    if (process.platform !== 'win32')  return;

    /**
     * @test {getPartition}
     */
    it(
        'Find all existing disk partitions',
        () => getPartition().should.be.containEql('C:\\')
    );

    /**
     * @test {getAppFolder}
     */
    it('Get possible paths of application installation',  () => {

        const path = getAppFolder();

        path.should.be.containEql('C:\\Program Files');

        if (parseInt( release() ) > 5)
            path.should.matchAny( /C:\\Users\\.+?\\AppData\\Local/ );
    });
});


describe('`which` command',  () => {

    const command = 'node dist/which node';


    it('Write to `stdout`',  () => {

        const path = (execSync( command ) + '').trim();

        isAbsolute( path ).should.be.true();

        path.should.be.endWith( NodeExe );

        existsSync( path ).should.be.true();
    });


    it('Write to a file',  () => {

        const log = execSync(`${command} -f test/example.ini`) + '';

        log.should.match( /node=[\s\S]+?node[\s\S]+-+\nSearch: \d+(\.\d+)?ms/ );

        (readFileSync('test/example.ini') + '').trim().should.be.equal(
            log.split('\n')[0]
        );
    });


    it('Write to NPM configuration',  () => {

        const log = execSync(`${command} -c`) + '';

        (execSync('npm get node') + '').trim().should.be.equal(
            log.split( /=|\n/ )[1]
        );
    });


    if (process.platform !== 'win32')
        it(
            'Find nothing with only empty output',
            ()  =>  (execSync(`${command}_unknown`) + '').trim().should.be.empty()
        );


    after(() => {

        unlinkSync('test/example.ini');

        execSync('npm config delete node');
    });
});
